package ua.com.juja.sqlcmd.controller;import ua.com.juja.sqlcmd.controller.command.*;import ua.com.juja.sqlcmd.model.DatabaseManager;import ua.com.juja.sqlcmd.view.View;import java.util.AbstractList;import java.util.LinkedList;import java.util.List;public class MainController {    View view;    DatabaseManager databaseManager;    Command[] commands;    public MainController(View view, DatabaseManager databaseManager, Command[] commands) {        this.view = view;        this.databaseManager = databaseManager;        this.commands = commands;    }    public void run() {        view.write("Привет юзер!");        view.write("Введи, пожалуйста имя базы данных, имя пользователя и пароль в формате: connect|database|userName|password");        boolean exit = false;        while (true) {            String input = view.read();            try {                handleInput(input);            } catch (ExitException e) {                break;            }            view.write("Введи команду (или help для помощи):");        }    }    private void handleInput(String input) {        for (Command command : commands) {            if (command.canProcess(input)) {                command.process(input);            }        }    }}